import { useReducer, useRef, useEffect } from "react";
import { useDispatch } from "react-redux";
import { loginActions } from "../../store/login-slice";
import Card from "../UI/Card/Card";
import Button from "../UI/Button/Button";
import Input from "../CommonInput/Input";
import classes from "./Login.module.css";

function emailReducer(state, action) {
  if (action.type === "USER_INPUT") {
    return { value: action.val, isValid: action.val.includes("@") };
  }
  if (action.type === "INPUT_BLUR") {
    return { value: state.value, isValid: state.value.includes("@") };
  }

  return { value: "", isValid: false };
}

function passwordReducer(state, action) {
  if (action.type === "USER_INPUT") {
    return { value: action.val, isValid: action.val.trim().length > 6 };
  }
  if (action.type === "INPUT_BLUR") {
    return { value: state.value, isValid: state.value.trim().length > 6 };
  }

  return { value: "", isValid: false };
}

function Login() {
  const [emailState, dispatchEmail] = useReducer(emailReducer, {
    value: "",
    isValid: null,
  });

  const [passwordState, dispatchPassword] = useReducer(passwordReducer, {
    value: "",
    isValid: null,
  });

  const emailInputRef = useRef();
  const passwordInputRef = useRef();

  //checking for form validity every 500ms
  useEffect(
    function () {
      const identifier = setTimeout(() => {
        setFormIsValid(emailState.isValid && passwordState.isValid);
      }, 500);

      return function clearTimer() {
        clearTimeout(identifier);
      };
    },
    //use effect dependencies - will re run every time below dependencies change.
    //Therefore useffect is guaranteed to use the latest state.
    [emailState.isValid, passwordState.isValid]
  );

  function submitHandler() {
    console.log("SUBMIT HANDLER");
  }
  function emailChangeHandler() {
    if (emailInputRef.current.value.includes("@")) {
    }
  }
  function validateEmailHandler() {}
  function passwordChangeHandler() {}
  function validatePasswordHandler() {}

  //DUMMY STATE
  const emailValid = true;
  const passwordValid = true;
  const emailValue = "Hi";
  const passwordValue = "Bye";

  return (
    <Card className={classes.login}>
      <form onSubmit={submitHandler}>
        <Input
          ref={emailInputRef}
          id="email"
          label="E-Mail"
          type="email"
          isValid={emailValid}
          value={emailValue}
          onChange={emailChangeHandler}
          onBlur={validateEmailHandler}
        />
        <Input
          ref={passwordInputRef}
          id="password"
          label="Password"
          type="password"
          isValid={passwordValid}
          value={passwordValue}
          onChange={passwordChangeHandler}
          onBlur={validatePasswordHandler}
        />

        <div className={classes.action}>
          <Button type="submit" className={classes.btn}>
            Login
          </Button>
        </div>
      </form>
    </Card>
  );
}

export default Login;
