import { useReducer, useState } from "react";
import { NavLink } from "react-router-dom";
import { useDispatch } from "react-redux";
import { loginActions } from "../../store/login-slice";
import classes from "./MainNavigation.module.css";

function profileReducer(action) {
  if (action.type === "SWITCH_OFF") {
    return { value: true };
  }
  if (action.type === "SWITCH_ON") {
    return { value: false };
  }
}

const MainNavigation = (props) => {
  const dispatch = useDispatch();

  const [profileState, dispatchProfile] = useReducer(profileReducer, {
    value: true,
  });

  function profileClickHandler() {
    console.log("Called");
    dispatchProfile({ type: "SWITCH_OFF" });
  }

  function logoutHandler() {
    dispatch(loginActions.LoginStateHandler());
    localStorage.removeItem("isLoggedIn");
    localStorage.removeItem("userId");
  }

  return (
    <header className={classes.header}>
      <div className={classes.logo}>Aerospace LogiX</div>
      <nav className={classes.nav}>
        <ul>
          {profileState.value && (
            <li>
              <NavLink
                to="/home/myProfile"
                className={(navData) =>
                  navData.isActive ? classes.active : ""
                }
                onClick={profileClickHandler}
              >
                My Profile
              </NavLink>
            </li>
          )}
          <li>
            <NavLink
              to="/login"
              className={(navData) => (navData.isActive ? classes.active : "")}
              onClick={logoutHandler}
            >
              Logout
            </NavLink>
          </li>
        </ul>
      </nav>
    </header>
  );
};

export default MainNavigation;
