import { useState, useRef } from "react";
import { useParams } from "react-router-dom";
import classes from "../Profile/MyProfile.module.css";
import loginStyles from "../Authentication/Login.module.css";
import Input from "../CommonInput/Input";
import Card from "../UI/Card/Card";
import Button from "../UI/Button/Button";
import styles from "../CommonInput/Input.module.css";

function Booking() {
  const nameInputRef = useRef();
  const ageInputRef = useRef();
  const planetInputRef = useRef();
  const dateInputRef = useRef();

  const [userName, setUserName] = useState(null);
  const [dob, setDob] = useState(null);

  function nameChangeHandler(event) {
    setUserName(event.target.value);
  }

  function dateChangeHandler(event) {
    setDob(event.target.value);
  }

  const params = useParams();
  console.log(params);
  const { shipId } = params;

  return (
    <Card className={loginStyles.login}>
      <h2 className={classes.h2}>Passenger Details</h2>
      <form onSubmit={null}>
        <Input
          ref={nameInputRef}
          id="name"
          label="Name"
          type="text"
          value={userName}
          onChange={nameChangeHandler}
        />
        <Input
          ref={nameInputRef}
          id="age"
          label="Age"
          type="number"
          value={null}
          onChange={null}
        />
        <div className={styles.control}>
          <label htmlFor="date">Date of Birth</label>
          <input
            ref={dateInputRef}
            id="date"
            label="Date of Birth"
            type="date"
            value={dob}
            onChange={dateChangeHandler}
          ></input>
        </div>
        <div className={loginStyles.action}>
          <Button type="submit" className={classes.btn}>
            Save
          </Button>
          <Button
            type="button"
            className={classes.btn}
            onClick={null}
          >
            Cancel
          </Button>
        </div>
      </form>
    </Card>
  );
}
export default Booking;
