import React, { useState, useRef } from "react";
import { useParams } from "react-router-dom";
import classes from "../Profile/MyProfile.module.css";
import loginStyles from "../Authentication/Login.module.css";
import Input from "../CommonInput/Input";
import Card from "../UI/Card/Card";
import Button from "../UI/Button/Button";
import styles from "../CommonInput/Input.module.css";

function Booking() {
  const nameInputRef = useRef();
  const ageInputRef = useRef();

  const [userName, setUserName] = useState(null);
  const [dob, setDob] = useState(null);

  const [disableCancel, setDisableCancel] = useState(false);
  const [passengerCount, setPassengerCount] = useState(1);

  function nameChangeHandler(event) {
    setUserName(event.target.value);
  }

  function dateChangeHandler(event) {
    setDob(event.target.value);
  }

  function onClickAddPassenger(){
    setDisableCancel(true);
  }

  const params = useParams();
  console.log(params);
  const { shipId } = params;

  return (
    <React.Fragment>
    <Card className={loginStyles.login}>
      <h2 className={classes.h2}>Passenger Details</h2>
      <form onSubmit={null}>
        <Input
          ref={nameInputRef}
          id="name"
          label="Name"
          type="text"
          value={userName}
          onChange={nameChangeHandler}
        />
        <Input
          ref={ageInputRef}
          id="age"
          label="Age"
          type="number"
          value={null}
          onChange={null}
        />
        <Input
          ref={null}
          id="email"
          label="Email"
          type="text"
          value={null}
          onChange={null}
        />
        <Input
          ref={null}
          id="phoneNumber"
          label="Phone Number"
          type="number"
          value={null}
          onChange={null}
        />
        <div className={loginStyles.action}>
          <Button type="submit" className={classes.btn}>
            Book
          </Button>
          <Button
            type="button"
            className={classes.btn}
            onClick={onClickAddPassenger}
          >
            Add Passenger
          </Button>
          {!disableCancel && <Button
            type="button"
            className={classes.btn}
            onClick={null}
          >
            Cancel
          </Button>}
        </div>
      </form>
    </Card>
    </React.Fragment>
  );
}
export default Booking;
