import { Route, Routes, Navigate, Link } from "react-router-dom";
import { useSelector } from "react-redux";
import Login from "./components/Authentication/Login";
import classes from "./components/UI/Button/Button.module.css";
import { useState } from "react";

function App() {

  const [signUpFlag, setSignUpFlag] = useState(true);
  const [loginF, setLoginF] = useState(false);

  const isAuthenticated = useSelector((state) => state.login.isAuthenticated);

  function postSignUpHandler() {
    console.log("POST");
    setSignUpFlag(true);
    setLoginF(true);
  }

  function revert(){
    setLoginF(true);
  }

  return (
    <Routes>
      <Route path="/" element={<Navigate replace to="/login" />} />
      <Route path="/login" element={<Login />}>
        <Route
          path=""
          element={
            <Link
              style={{ textDecoration: "none" }}
              className={classes.button}
              to={"/signUp"}
              onClick={revert}
            >
              SignUp
            </Link>
          }
        />
      </Route>
      <Route
        path="/signUp"
        element={
          <Login signUpFlag={signUpFlag} postSignUp={postSignUpHandler} loginF={loginF} />
        }
      />
    </Routes>
  );
}

export default App;
