import React, { useState, useRef } from "react";
import { useParams } from "react-router-dom";
import classes from "../Profile/MyProfile.module.css";
import loginStyles from "../Authentication/Login.module.css";
import Input from "../CommonInput/Input";
import Card from "../UI/Card/Card";
import Button from "../UI/Button/Button";
import Layout from "../layout/Layout";

function Booking() {
  const nameInputRef = useRef();
  const ageInputRef = useRef();
  const emailInputRef = useRef();
  const phoneInputRef = useRef();

  const [userName, setUserName] = useState(null);
  const [age, setAge] = useState(null);
  const [email, setEmail] = useState(null);
  const [phoneNumber, setPhoneNumber] = useState(null);

  const [passengerCount, setPassengerCount] = useState(1);

  function nameChangeHandler(event) {
    setUserName(event.target.value);
  }
  function ageChangeHandler(event) {
    setAge(event.target.value);
  }
  function emailChangeHandler(event) {
    setEmail(event.target.value);
  }
  function phoneNumberChangeHandler(event) {
    setPhoneNumber(event.target.value);
  }

  function onClickAddPassenger() {
    setPassengerCount(passengerCount + 1);
  }

  const params = useParams();
  const { shipId } = params;

  return (
    <Layout>
      <Card className={loginStyles.login}>
        <h2 className={classes.h2}>Passenger 1 - Details</h2>
        <form onSubmit={null}>
          <Input
            ref={nameInputRef}
            id="name1"
            label="Name"
            type="text"
            value={nameInputRef.current.value}
            onChange={nameChangeHandler}
          />
          <Input
            ref={ageInputRef}
            id="age1"
            label="Age"
            type="number"
            value={age}
            onChange={ageChangeHandler}
          />
          <Input
            ref={emailInputRef}
            id="email"
            label="Email"
            type="text"
            value={email}
            onChange={setEmail}
          />
          <Input
            ref={phoneInputRef}
            id="phoneNumber"
            label="Phone Number"
            type="number"
            value={phoneNumber}
            onChange={phoneNumberChangeHandler}
          />
          {passengerCount === 2 && (
            <h2 className={classes.h2}>Passenger 2 - Details</h2>
          )}
          {passengerCount === 2 && (
            <Input
              ref={nameInputRef}
              id="name2"
              label="Name"
              type="text"
              value={userName}
              onChange={nameChangeHandler}
            />
          )}
          {passengerCount === 2 && (
            <Input
              ref={ageInputRef}
              id="age2"
              label="Age"
              type="number"
              value={null}
              onChange={null}
            />
          )}
          <div className={loginStyles.action}>
            <Button type="submit" className={classes.btn}>
              Book
            </Button>
            {passengerCount < 2 && <Button
              type="button"
              className={classes.btn}
              onClick={onClickAddPassenger}
            >
              Add Passenger
            </Button>}
            <Button type="button" className={classes.btn} onClick={null}>
              Cancel
            </Button>
          </div>
        </form>
      </Card>
    </Layout>
  );
}
export default Booking;
