import Layout from "../layout/Layout";
import { useRef, useState, useReducer, useEffect } from "react";
import classes from "../Home/Home.module.css";
import styles from "../CommonInput/Input.module.css";
import Card from "../UI/Card/Card";
import Button from "../UI/Button/Button";
import useHttp from "../../hooks/use-http";
import Input from "../CommonInput/Input";

function fromReducer(state, action) {
  if (action.type === "USER_INPUT") {
    return { value: action.val, isValid: action.val.trim() !== "" };
  }
  if (action.type === "INPUT_BLUR") {
    return { value: state.value, isValid: state.value.trim() !== "" };
  }
  if (action.type === "CLEAR") {
    return { value: "", isValid: null };
  }

  return { value: "", isValid: false };
}

function toReducer(state, action) {
  if (action.type === "USER_INPUT") {
    return { value: action.val, isValid: action.val.trim() !== "" };
  }
  if (action.type === "INPUT_BLUR") {
    return { value: state.value, isValid: state.value.trim() !== "" };
  }
  if (action.type === "CLEAR") {
    return { value: "", isValid: null };
  }

  return { value: "", isValid: false };
}

function classReducer(state, action) {
  if (action.type === "USER_INPUT") {
    return { value: action.val, isValid: action.val.trim() !== "" };
  }
  if (action.type === "INPUT_BLUR") {
    return { value: state.value, isValid: state.value.trim() !== "" };
  }
  if (action.type === "CLEAR") {
    return { value: "", isValid: null };
  }

  return { value: "", isValid: false };
}

function journeyDateReducer(state, action) {
  if (action.type === "USER_INPUT") {
    return {
      value: action.val,
      isValid: action.val.trim() !== "",
    };
  }
  if (action.type === "INPUT_BLUR") {
    return {
      value: state.value,
      isValid: state.value.trim() !== "",
    };
  }
  if (action.type === "CLEAR") {
    return { value: "", isValid: null };
  }

  return { value: "", isValid: false };
}

function Home() {
  const [routes, setRoutes] = useState([]);
  const [formIsValid, setFormIsValid] = useState(false);

  const [fromState, dispatchFrom] = useReducer(fromReducer, {
    value: "",
    isValid: null,
  });

  const [toState, dispatchTo] = useReducer(toReducer, {
    value: "",
    isValid: null,
  });

  const [classState, dispatchClass] = useReducer(classReducer, {
    value: "",
    isValid: null,
  });

  const [dateState, dispatchDate] = useReducer(journeyDateReducer, {
    value: "",
    isValid: null,
  });

  const {
    isLoading,
    error,
    sendRequest: sendTaskRequest,
    response,
    resetError,
    resetResponse,
  } = useHttp();

  const fromInputRef = useRef();
  const toInputRef = useRef();
  const classInputRef = useRef();
  const dateInputRef = useRef();

  function fromChangeHandler(event) {
    dispatchFrom({ type: "USER_INPUT", val: event.target.value });
  }
  function validateFromHandler() {
    dispatchFrom({ type: "INPUT_BLUR" });
  }
  function toChangeHandler(event) {
    dispatchTo({ type: "USER_INPUT", val: event.target.value });
  }
  function validateToHandler() {
    dispatchTo({ type: "INPUT_BLUR" });
  }
  function classChangeHandler(event) {
    dispatchClass({ type: "USER_INPUT", val: event.target.value });
  }
  function validateClassHandler() {
    dispatchClass({ type: "INPUT_BLUR" });
  }
  function dateChangeHandler(event) {
    dispatchDate({ type: "USER_INPUT", val: event.target.value });
  }
  function validateDateHandler() {
    dispatchDate({ type: "INPUT_BLUR" });
  }

  //checking for form validity every 500ms
  useEffect(
    function () {
      const identifier = setTimeout(() => {
        setFormIsValid(
          fromState.isValid &&
            toState.isValid &&
            classState.isValid &&
            dateState.isValid
        );
      }, 200);

      return function clearTimer() {
        clearTimeout(identifier);
      };
    },
    //use effect dependencies - will re run every time below dependencies change.
    //Therefore useffect is guaranteed to use the latest state.
    [
      fromState.isValid,
      toState.isValid,
      classState.isValid,
      dateState.isValid,
      formIsValid,
    ]
  );

  //Request to backend for login and sign-up
  const SearchShipsHandler = async (requestJson) => {
    sendTaskRequest({
      url: "http://localhost:8080/searchShips",
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
      body: {
        from: requestJson.enteredFrom,
        to: requestJson.enteredTo,
        class: requestJson.enteredClass,
        journeyDate: requestJson.enteredDate,
      },
      errorMsg: "Request failed!",
    });
  };

  function submitHandler(event) {
    event.preventDefault();

    const enteredFrom = fromState.value;
    const enteredTo = toState.value;
    const enteredClass = classState.value;
    const enteredDate = dateState.value;

    console.log(enteredFrom + enteredTo + enteredClass + enteredDate);

    if (formIsValid) {
      const requestJson = {
        enteredFrom,
        enteredTo,
        enteredClass,
        enteredDate,
      };

      SearchShipsHandler(requestJson);
      setFormIsValid(false);

      dispatchFrom({ type: "CLEAR" });
      dispatchTo({ type: "CLEAR" });
      dispatchClass({ type: "CLEAR" });
      dispatchDate({ type: "CLEAR" });
    }
  }

  const fromControlClasses = `${styles.control} ${
    fromState.isValid = true ? "" : styles.invalid
  }`;
  const toControlClasses = `${styles.control} ${
    toState.isValid = true ? "" : styles.invalid
  }`;
  const classControlClasses = `${styles.control} ${
    classState.isValid = true ? "" : styles.invalid
  }`;
  const dateControlClasses = `${styles.control} ${
    dateState.isValid ? "" : styles.invalid
  }`;

  return (
    <Layout>
      <section className={classes.summary}>
        <form onSubmit={submitHandler}>
          {!fromState.isValid && <p color="red">Error</p>}
          <div className={fromControlClasses}>
            <label>From</label>
            <select
              ref={fromInputRef}
              className={classes.select}
              name="fromList"
              id="from"
              value={fromState.value}
              onChange={fromChangeHandler}
