import Layout from "../layout/Layout";
import { useRef, useState, useReducer, useEffect } from "react";
import classes from "../Home/Home.module.css";
import styles from "../CommonInput/Input.module.css";
import Card from "../UI/Card/Card";
import Button from "../UI/Button/Button";
import useHttp from "../../hooks/use-http";
import Input from "../CommonInput/Input";

const isEmpty = (value) => value.trim() === "";

function Home() {
  const [routes, setRoutes] = useState([]);
  const [formInputsValidity, setFormInputsValidity] = useState({
    from: null,
    to: null,
    class: null,
    date: null,
  });

  const {
    isLoading,
    error,
    sendRequest: sendTaskRequest,
    response,
    resetError,
    resetResponse,
  } = useHttp();

  const fromInputRef = useRef();
  const toInputRef = useRef();
  const classInputRef = useRef();
  const dateInputRef = useRef();

  //Request to backend for login and sign-up
  const SearchShipsHandler = async (requestJson) => {
    sendTaskRequest({
      url: "http://localhost:8080/searchShips",
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
      body: {
        from: requestJson.enteredFrom,
        to: requestJson.enteredTo,
        class: requestJson.enteredClass,
        journeyDate: requestJson.enteredDate,
      },
      errorMsg: "Request failed!",
    });
  };

  function submitHandler(event) {
    event.preventDefault();

    const enteredFrom = fromInputRef.current.value;
    const enteredTo = toInputRef.current.value;
    const enteredClass = classInputRef.current.value;
    const enteredDate = dateInputRef.current.value;

    const enteredFromIsValid = !isEmpty(enteredFrom);
    const enteredToIsValid = !isEmpty(enteredTo);
    const enteredClassIsValid = !isEmpty(enteredClass);
    const enteredDateIsValid = !isEmpty(enteredDate);

    setFormInputsValidity({
      from: enteredFromIsValid,
      to: enteredToIsValid,
      class: enteredClassIsValid,
      date: enteredDateIsValid,
    });

    const formIsValid =
      enteredFromIsValid &&
      enteredToIsValid &&
      enteredClassIsValid &&
      enteredDateIsValid;

    if (formIsValid) {
      const requestJson = {
        enteredFrom,
        enteredTo,
        enteredClass,
        enteredDate,
      };

      SearchShipsHandler(requestJson);
      fromInputRef.current.value="";
      toInputRef.current.value="";
      classInputRef.current.value="";
      dateInputRef.current.value="";
    }
  }

  const fromControlClasses = `${styles.control} ${(formInputsValidity.from =
    true ? "" : styles.invalid)}`;
  const toControlClasses = `${styles.control} ${(formInputsValidity.to = true
    ? ""
    : styles.invalid)}`;
  const classControlClasses = `${styles.control} ${(formInputsValidity.class =
    true ? "" : styles.invalid)}`;
  const dateControlClasses = `${styles.control} ${
    formInputsValidity.date ? "" : styles.invalid
  }`;

  return (
    <Layout>
      <section className={classes.summary}>
        <h3 className={classes.heading}>Book a Ship</h3>
        <form onSubmit={submitHandler}>
          {formInputsValidity.from === false && (
            <p className={classes.error}>Please select a valid option!</p>
          )}
          <div className={fromControlClasses}>
            <label>From</label>
            <select
              ref={fromInputRef}
              className={classes.select}
              name="fromList"
              id="from"
            >
              <option value="" style={{ color: "grey" }}>
                Select an Option
              </option>
              <option value="Earth" style={{ color: "black" }}>
                Earth
              </option>
              <option value="Mars" style={{ color: "black" }}>
                Mars
              </option>
              <option value="Moon" style={{ color: "black" }}>
                Moon
              </option>
              <option value="Ceres" style={{ color: "black" }}>
                Ceres
              </option>
              <option value="Europa" style={{ color: "black" }}>
                Europa
              </option>
              <option value="Titan" style={{ color: "black" }}>
                Titan
              </option>
              <option value="EB-15" style={{ color: "black" }}>
                EB-15
              </option>
              <option value="ISS" style={{ color: "black" }}>
                ISS
              </option>
              <option value="Kepler-186F" style={{ color: "black" }}>
                Kepler-186F
              </option>
            </select>
          </div>
          <div className={toControlClasses}>
            <label>To</label>
            <select
              ref={toInputRef}
              className={classes.select}
              name="toList"
              id="to"
            >
              <option value="" style={{ color: "grey" }}>
                Select an Option
              </option>
              <option value="Earth" style={{ color: "black" }}>
                Earth
              </option>
              <option value="Mars" style={{ color: "black" }}>
                Mars
              </option>
              <option value="Moon" style={{ color: "black" }}>
                Moon
              </option>
              <option value="Ceres" style={{ color: "black" }}>
                Ceres
              </option>
              <option value="Europa" style={{ color: "black" }}>
                Europa
              </option>
              <option value="Titan" style={{ color: "black" }}>
                Titan
              </option>
              <option value="EB-15" style={{ color: "black" }}>
                EB-15
              </option>
              <option value="ISS" style={{ color: "black" }}>
                ISS
              </option>
              <option value="Kepler-186F" style={{ color: "black" }}>
                Kepler-186F
              </option>
            </select>
          </div>
          <div className={classControlClasses}>
            <label>Class</label>
            <select
              ref={classInputRef}
              className={classes.select}
              name="classList"
              id="class"
            >
              <option value="" style={{ color: "grey" }}>
                Select an Option
              </option>
              <option value="Economy" style={{ color: "black" }}>
                Economy
              </option>
              <option value="Premium" style={{ color: "black" }}>
                Premium
              </option>
              <option value="Executive" style={{ color: "black" }}>
                Executive
              </option>
            </select>
          </div>
          <Input
            ref={dateInputRef}
            id="date"
            label="Journey Date"
            type="date"
          ></Input>
          <div className={classes.action}>
            <Button type="submit" className={classes.actions}>
              Search Ships
            </Button>
          </div>
        </form>
      </section>
      <section className={classes.ships}>
        <Card>
          <ul>Wayayayay</ul>
        </Card>
      </section>
    </Layout>
  );
}
export default Home;
