import React, { useState, useRef } from "react";
import { useParams } from "react-router-dom";
import classes from "../Profile/MyProfile.module.css";
import loginStyles from "../Authentication/Login.module.css";
import Input from "../CommonInput/Input";
import Card from "../UI/Card/Card";
import Button from "../UI/Button/Button";
import styles from "../CommonInput/Input.module.css";

function Booking() {
  const nameInputRef = useRef();
  const ageInputRef = useRef();

  const [userName, setUserName] = useState(null);
  const [dob, setDob] = useState(null);

  const [passengerCount, setPassengerCount] = useState(1);

  function nameChangeHandler(event) {
    setUserName(event.target.value);
  }

  function dateChangeHandler(event) {
    setDob(event.target.value);
  }

  function onClickAddPassenger(){
    setPassengerCount(passengerCount + 1)
  }

  const params = useParams();
  console.log(params);
  const { shipId } = params;

  return (
    <React.Fragment>
    <Card className={loginStyles.login}>
      <h2 className={classes.h2}>Passenger 1 - Details</h2>
      <form onSubmit={null}>
        <Input
          ref={nameInputRef}
          id="name1"
          label="Name"
          type="text"
          value={userName}
          onChange={nameChangeHandler}
        />
        <Input
          ref={ageInputRef}
          id="age1"
          label="Age"
          type="number"
          value={null}
          onChange={null}
        />
        <Input
          ref={null}
          id="email"
          label="Email"
          type="text"
          value={null}
          onChange={null}
        />
        <Input
          ref={null}
          id="phoneNumber"
          label="Phone Number"
          type="number"
          value={null}
          onChange={null}
        />
        {passengerCount !== 1 && <h2 className={classes.h2}>Passenger 2 - Details</h2>}
        {passengerCount !== 1 && <Input
          ref={nameInputRef}
          id="name2"
          label="Name"
          type="text"
          value={userName}
          onChange={nameChangeHandler}
        />}
        {passengerCount !== 1 && <Input
          ref={ageInputRef}
          id="age2"
          label="Age"
          type="number"
          value={null}
          onChange={null}
        />}
        {passengerCount !== 1 || passengerCount !== 2 && <h2 className={classes.h2}>Passenger 3 - Details</h2>}
        {passengerCount !== 1 || passengerCount !== 2 && <Input
          ref={nameInputRef}
          id="name3"
          label="Name"
          type="text"
          value={userName}
          onChange={nameChangeHandler}
        />}
        {passengerCount !== 1 || passengerCount !== 2 && <Input
          ref={ageInputRef}
          id="age3"
          label="Age"
          type="number"
          value={null}
          onChange={null}
        />}
        {passengerCount === 4 && <h2 className={classes.h2}>Passenger 4 - Details</h2>}
        {passengerCount === 4 && <Input
          ref={nameInputRef}
          id="name4"
          label="Name"
          type="text"
          value={userName}
          onChange={nameChangeHandler}
        />}
        {passengerCount === 4 && <Input
          ref={ageInputRef}
          id="age4"
          label="Age"
          type="number"
          value={null}
          onChange={null}
        />}
        <div className={loginStyles.action}>
          <Button type="submit" className={classes.btn}>
            Book
          </Button>
          <Button
            type="button"
            className={classes.btn}
            onClick={onClickAddPassenger}
          >
            Add Passenger
          </Button>
          <Button
            type="button"
            className={classes.btn}
            onClick={null}
          >
            Cancel
          </Button>
        </div>
      </form>
    </Card>
    </React.Fragment>
  );
}
export default Booking;
