import React, { useState, useRef } from "react";
import { useParams } from "react-router-dom";
import classes from "../Profile/MyProfile.module.css";
import loginStyles from "../Authentication/Login.module.css";
import Input from "../CommonInput/Input";
import Card from "../UI/Card/Card";
import Button from "../UI/Button/Button";
import Layout from "../layout/Layout";
import { useNavigate } from "react-router-dom";

function Booking() {
  const navigate = useNavigate();

  const nameInputRef = useRef();
  const ageInputRef = useRef();
  const emailInputRef = useRef();
  const phoneInputRef = useRef();

  const name2InputRef = useRef();
  const age2InputRef = useRef();

  const [userName, setUserName] = useState("");
  const [age, setAge] = useState("");
  const [email, setEmail] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");

  const [user2Name, setUser2Name] = useState("");
  const [age2, setAge2] = useState("");

  const [formIsValid, setFormIsValid] = useState(true);

  const [passengerCount, setPassengerCount] = useState(1);

  function nameChangeHandler(event) {
    setUserName(event.target.value);
  }
  function ageChangeHandler(event) {
    setAge(event.target.value);
  }
  function emailChangeHandler(event) {
    setEmail(event.target.value);
  }
  function phoneNumberChangeHandler(event) {
    setPhoneNumber(event.target.value);
  }
  function name2ChangeHandler(event) {
    setUser2Name(event.target.value);
  }
  function age2ChangeHandler(event) {
    setAge2(event.target.value);
  }

  function onClickAddPassenger() {
    setPassengerCount(passengerCount + 1);
  }
  function onClickCancelHandler() {
    navigate("/home");
  }

  function bookingSubmitHandler(event) {
    event.preventDefault();

    console.log(passengerCount);

    const userNameIsValid = userName.trim() !== "";
    const ageIsValid = age.trim() > 0;
    const emailIsValid = email.trim().includes("@");
    const phoneNumberIsValid = phoneNumber.trim() !== "";

    setFormIsValid(
      userNameIsValid && ageIsValid && emailIsValid && phoneNumberIsValid
    );

    console.log(formIsValid);

    if (passengerCount === 2) {
      const user2NameIsValid = user2Name.trim() !== "";
      const age2IsValid = age2.trim() > 0;

      setFormIsValid(
        userNameIsValid &&
          ageIsValid &&
          emailIsValid &&
          phoneNumberIsValid &&
          user2NameIsValid &&
          age2IsValid
      );
    }

    console.log(formIsValid);

    if (formIsValid) {
      console.log("WORKING FINE");
    }
  }

  const params = useParams();
  const { shipId } = params;

  return (
    <Layout>
      <Card className={loginStyles.login}>
        <h2 className={classes.h2}>Passenger 1 - Details</h2>
        <form onSubmit={bookingSubmitHandler}>
          <Input
            ref={nameInputRef}
            id="name1"
            label="Name"
            type="text"
            value={userName}
            onChange={nameChangeHandler}
          />
          <Input
            ref={ageInputRef}
            id="age1"
            label="Age"
            type="number"
            value={age}
            onChange={ageChangeHandler}
          />
          <Input
            ref={emailInputRef}
            id="email"
            label="Email"
            type="text"
            value={email}
            onChange={emailChangeHandler}
          />
          <Input
            ref={phoneInputRef}
            id="phoneNumber"
            label="Phone Number"
            type="number"
            value={phoneNumber}
            onChange={phoneNumberChangeHandler}
          />
          {passengerCount === 2 && (
            <h2 className={classes.h2}>Passenger 2 - Details</h2>
          )}
          {passengerCount === 2 && (
            <Input
              ref={name2InputRef}
              id="name2"
              label="Name"
              type="text"
              value={user2Name}
              onChange={name2ChangeHandler}
            />
          )}
          {passengerCount === 2 && (
            <Input
              ref={age2InputRef}
              id="age2"
              label="Age"
              type="number"
              value={age2}
              onChange={age2ChangeHandler}
            />
          )}
          <div className={loginStyles.action}>
            <Button type="submit" className={classes.btn}>
              Book
            </Button>
            {passengerCount < 2 && (
              <Button
                type="button"
                className={classes.btn}
                onClick={onClickAddPassenger}
              >
                Add Passenger
              </Button>
            )}
            <Button
              type="button"
              className={classes.btn}
              onClick={onClickCancelHandler}
            >
              Cancel
            </Button>
          </div>
        </form>
      </Card>
    </Layout>
  );
}
export default Booking;
