import Tickets from "./Tickets";
import classes from "./MyBooking.module.css";
import Layout from "../layout/Layout";
import { useSelector } from "react-redux";
import { useEffect, useState } from "react";
import useHttp from "../../hooks/use-http";
import TicketList from "./TicketList";

var ticketId = null
function MyBookings() {
  const {
    isLoading: bookingIsLoading,
    error: bookingError,
    sendRequest: sendBookingRequest,
    response: bookingResponse,
    resetError: resetBookingResponse,
    resetResponse: resetBookingError,
  } = useHttp();

  const[showPrompt, setShowPrompt] = useState(false);

  function onTicketCancelHandler(event){
    ticketId = event.target.id;
    setShowPrompt(true);
  }

  const userEmail = useSelector((state) => state.login.userId);

  //Request to backend for bookings data
  const fetchBookingDetails = async (requestJson) => {
    sendBookingRequest({
      url: "http://localhost:8080/api/protected/fetchBookingDetails",
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
      body: {
        userEmail: requestJson.userEmail,
      },
      errorMsg: "Request failed!",
    });
  };

  useEffect(() => {
    const requestJson = { userEmail };
    fetchBookingDetails(requestJson);
  }, []);

  const ticketCancelModalActions = (
    <div className={classes.actions}>
      <Button className={classes.btn} onClick={cancellationHandler}>
        Yes
      </Button>
      <Button className={classes.btn} onClick={closeTicketCancelModal}>
        No
      </Button>
    </div>
  );

  const ticketCancelModalContent = (
    <React.Fragment>
      <div className={classes.total}>
        <span>Are you sure? You will be refunded within 5-7 days.</span>
      </div>
    </React.Fragment>
  );

  return (
    <Layout>
      <TicketList bookingList={bookingResponse} />
    </Layout>
  );
}
export default MyBookings;
