import Layout from "../layout/Layout";
import classes from "./MyProfile.module.css";
import Button from "../UI/Button/Button";
import { useEffect, useState, useRef } from "react";
import moment from "moment";
import styles from "../CommonInput/Input.module.css";
import Card from "../UI/Card/Card";
import useHttp from "../../hooks/use-http";
import Input from "../CommonInput/Input";
import loginStyles from "../Authentication/Login.module.css";
import { useSelector } from "react-redux";

function MyProfile() {

  const {
    isLoading,
    error,
    sendRequest: sendTaskRequest,
    response,
    resetError,
    resetResponse,
  } = useHttp();

  //Request to backend for profile data
  const FetchProfileHandler = async (requestJson) => {
    sendTaskRequest({
      url: "http://localhost:8080/fetchProfileDetails",
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
      body: {
        email: requestJson.userEmail,
      },
      errorMsg: "Request failed!",
    });
  };

  console.log(response.userName);


  const [enableForm, setEnableForm] = useState(false);
  const [userName, setUserName] = useState(response.userName);

  const userEmail = useSelector((state) => state.login.userId);

  const nameInputRef = useRef();
  const emailInputRef = useRef();
  const planetInputRef = useRef();
  const dateInputRef = useRef();

  useEffect(() => {
    const requestJson = { userEmail };
    FetchProfileHandler(requestJson);
  }, []);

  function onEditHandler() {
    setEnableForm(true);
  }

  function disableFormHandler() {
    setEnableForm(false);
  }

  function nameChangeHandler(event){
    setUserName(event.target.value)
  }

  return (
    <div>
      <Layout>
        {!enableForm && <div>
          <h2 className={classes.h2}>My Profile</h2>
          <div className={classes.control}>
            <label>Name : </label>
            <label>{response.userName}</label>
          </div>
          <div className={classes.control}>
            <label>E-Mail : </label>
            <label>{response.userEmail}</label>
          </div>
          <div className={classes.control}>
            <label>Password : </label>
            <label>{response.userPassword}</label>
          </div>
          <div className={classes.control}>
            <label>Base Planet : </label>
            <label>{response.basePlanet}</label>
          </div>
          <div className={classes.control}>
            <label>Date of Birth : </label>
            <label>{response.dob}</label>
          </div>
          <div className={loginStyles.action}>
            <Button
              type="button"
              className={classes.button}
              onClick={onEditHandler}
            >
              Edit
            </Button>
            <Button
              type="button"
              className={classes.button}
              onClick={disableFormHandler}
            >
              Change Password
            </Button>
          </div>
        </div>}

        {enableForm && (
          <Card className={loginStyles.login}>
            <form onSubmit={null}>
              <Input
                ref={nameInputRef}
                id="name"
                label="Name"
                type="text"
                value={userName}
                onChange={nameChangeHandler}
              />
              <Input
                ref={emailInputRef}
                id="email"
                label="E-Mail"
                type="email"
                value={response.userEmail}
              />
              <div className={styles.control}>
                <label>Base Planet</label>
                <select
                  ref={planetInputRef}
                  className={loginStyles.select}
                  name="planetList"
                  id="planets"
                  value={response.basePlanet}
                >
                  <option value="" style={{ color: "grey" }}>
                    Select an Option
                  </option>
                  <option value="Earth" style={{ color: "black" }}>
                    Earth
                  </option>
                  <option value="Mars" style={{ color: "black" }}>
                    Mars
                  </option>
                  <option value="Moon" style={{ color: "black" }}>
                    Moon
                  </option>
                  <option value="Ceres" style={{ color: "black" }}>
                    Ceres
                  </option>
                  <option value="Europa" style={{ color: "black" }}>
                    Europa
                  </option>
                  <option value="Titan" style={{ color: "black" }}>
                    Titan
                  </option>
                  <option value="EB-15" style={{ color: "black" }}>
                    EB-15
                  </option>
                  <option value="ISS" style={{ color: "black" }}>
                    ISS
                  </option>
                  <option value="Kepler-186F" style={{ color: "black" }}>
                    Kepler-186F
                  </option>
                </select>
              </div>
              <div className={styles.control}>
                <label htmlFor="date">Date of Birth</label>
                <input
                  ref={dateInputRef}
                  id="date"
                  label="Date of Birth"
                  type="date"
                  value={response.dob}
                  max={moment().format("YYYY-MM-DD")}
                ></input>
              </div>
              <div className={loginStyles.action}>
                <Button type="submit" className={classes.btn}>
                  Save
                </Button>
                <Button
                  type="button"
                  className={classes.btn}
                  onClick={disableFormHandler}
                >
                  Cancel
                </Button>
              </div>
            </form>
          </Card>
        )}
      </Layout>
    </div>
  );
}
export default MyProfile;
