import { Route, Routes, Navigate, Link, useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import Login from "./components/Authentication/Login";
import classes from "./components/UI/Button/Button.module.css";
import { useState } from "react";
import Home from "./components/Home/Home";

function App() {
  const navigate = useNavigate();
  const [signUpFlag, setSignUpFlag] = useState(true);
  const [navigateLoginF, setNavigateLoginF] = useState(true);
  const [navigateHomeF, setNavigateHomeF] = useState(true);

  const isAuthenticated = useSelector((state) => state.login.isAuthenticated);

  function postSignUpHandler() {
    setSignUpFlag(true);
    setNavigateLoginF(true);
  }

  function resetNavigateLoginF() {
    setNavigateLoginF(false);
  }

  function resetNavigateHomeF(){
    setNavigateHomeF(false);
  }

  function homeNavigation() {
    console.log(isAuthenticated);
    if (isAuthenticated) {
      navigate("/home");
    }
  }

  return (
    <Routes>
      <Route path="/" element={<Navigate replace to="/login" />} />
      <Route path="/login" element={<Login homeNavigate={homeNavigation} />}>
        <Route
          path=""
          element={
            <Link
              style={{ textDecoration: "none" }}
              className={classes.button}
              to={"/signUp"}
              onClick={resetNavigateLoginF}
            >
              New User?
            </Link>
          }
        />
      </Route>
      <Route
        path="/signUp"
        element={
          <Login
            signUpFlag={signUpFlag}
            postSignUp={postSignUpHandler}
            loginF={navigateLoginF}
            homeF = {navigateHomeF}
          />
        }
      >
        <Route
          path=""
          element={
            <Link
              style={{ textDecoration: "none" }}
              className={classes.button}
              to={"/login"}
              onClick={postSignUpHandler}
            >
              Back
            </Link>
          }
        />
      </Route>
      <Route path="/home" element={<Home />} />
    </Routes>
  );
}

export default App;
