import Layout from "../layout/Layout";
import classes from "./MyProfile.module.css";
import Button from "../UI/Button/Button";
import { useState } from "react";

function MyProfile() {
  const [enableForm, setEnableForm] = useState(false);

  function onEditHandler() {
    setEnableForm(true);
  }

  return (
    <div>
      <Layout>
        <h2 className={classes.h2}>My Profile</h2>
        <div className={classes.control}>
          <label>Name : </label>
          <label>Shankar Narayanan</label>
        </div>
        <div className={classes.control}>
          <label>E-Mail : </label>
          <label>shankarvenkat0512@gmail.com</label>
        </div>
        <div className={classes.control}>
          <label>Password : </label>
          <label>Shanky@0512</label>
        </div>
        <div className={classes.control}>
          <label>Base Planet : </label>
          <label>Earth</label>
        </div>
        <div className={classes.control}>
          <label>Date of Birth : </label>
          <label>05/12/1998</label>
        </div>
        <Button className={classes.button} onClick={onEditHandler}>
          Edit
        </Button>

        <Card className={classes.login}>
          {(error || response.responseMessage === "SIGNUP_SUCCESSFUL") && (
            <Modal onClose={props.onClose}>
              {cartModalContent}
              {modalActions}
            </Modal>
          )}
          <form onSubmit={submitHandler}>
            <Input
              ref={nameInputRef}
              id="name"
              label="Name"
              type="text"
              isValid={nameState.isValid}
              value={nameState.value}
              onChange={nameChangeHandler}
              onBlur={validateNameHandler}
            />
            )
            <Input
              ref={emailInputRef}
              id="email"
              label="E-Mail"
              type="email"
              isValid={emailState.isValid}
              value={emailState.value}
              onChange={emailChangeHandler}
              onBlur={validateEmailHandler}
            />
            <Input
              ref={passwordInputRef}
              id="password"
              label="Password"
              type="password"
              isValid={passwordState.isValid}
              value={passwordState.value}
              onChange={passwordChangeHandler}
              onBlur={validatePasswordHandler}
            />
            <Input
              ref={rePasswordInputRef}
              id="rePassword"
              label="Re-Password"
              type="password"
              isValid={rePasswordState.isValid}
              value={rePasswordState.value}
              onChange={rePasswordChangeHandler}
              onBlur={validateRePasswordHandler}
            />
            )
            <div className={styles.control}>
              <label>Base Planet</label>
              <select
                ref={planetInputRef}
                className={classes.select}
                name="planetList"
                id="planets"
                value={planetState.value}
                onChange={planetChangeHandler}
                onBlur={validatePlanetHandler}
              >
                <option value="" style={{ color: "grey" }}>
                  Select an Option
                </option>
                <option value="Earth" style={{ color: "black" }}>
                  Earth
                </option>
                <option value="Mars" style={{ color: "black" }}>
                  Mars
                </option>
                <option value="Moon" style={{ color: "black" }}>
                  Moon
                </option>
                <option value="Ceres" style={{ color: "black" }}>
                  Ceres
                </option>
                <option value="Europa" style={{ color: "black" }}>
                  Europa
                </option>
                <option value="Titan" style={{ color: "black" }}>
                  Titan
                </option>
                <option value="EB-15" style={{ color: "black" }}>
                  EB-15
                </option>
                <option value="ISS" style={{ color: "black" }}>
                  ISS
                </option>
                <option value="Kepler-186F" style={{ color: "black" }}>
                  Kepler-186F
                </option>
              </select>
            </div>
            )
            <div className={styles.control}>
              <label htmlFor="date">Date of Birth</label>
              <input
                ref={dateInputRef}
                id="date"
                label="Journey Date"
                type="date"
                max={moment().format("YYYY-MM-DD")}
              ></input>
            </div>
            )
            <div className={classes.action}>
              <Button type="submit" className={classes.btn}>
                Sign in
              </Button>
              <Outlet />
            </div>
            )
            <div className={classes.action}>
              <Button type="submit" className={classes.btn}>
                Sign up
              </Button>
              <Outlet />
            </div>
            )
          </form>
        </Card>
      </Layout>
    </div>
  );
}
export default MyProfile;
