import { Route, Routes, Navigate, Link } from "react-router-dom";
import { useSelector } from "react-redux";
import Login from "./components/Authentication/Login";
import classes from "./components/UI/Button/Button.module.css";

var signUpFlag = false;
function App() {
  const isAuthenticated = useSelector((state) => state.login.isAuthenticated);
  if (!isAuthenticated) {
    signUpFlag = true;
  }
  function postSignUpHandler() {
    console.log(isAuthenticated);
    signUpFlag = false;
    console.log(signUpFlag);
  }

  return (
    <Routes>
      <Route path="/" element={<Navigate replace to="/login" />} />
      <Route path="/login" element={<Login />}>
        <Route
          path=""
          element={
            <Link
              style={{ textDecoration: "none" }}
              className={classes.button}
              to={"/signUp"}
            >
              SignUp
            </Link>
          }
        />
      </Route>
      <Route
        path="/signUp"
        element={
          <Login signUpFlag={signUpFlag} postSignUp={postSignUpHandler} />
        }
      >
        <Route path="/signUp" element={<Navigate replace to="/login" />} />
      </Route>
    </Routes>
  );
}

export default App;
