import Tickets from "./Tickets";
import classes from "./MyBooking.module.css";
import Layout from "../layout/Layout";
import { useSelector } from "react-redux";
import { useEffect } from "react";

function MyBookings() {

  const {
    isLoading : bookingIsLoading,
    error : bookingError,
    sendRequest: sendBookingRequest,
    response : bookingResponse,
    resetError : resetBookingResponse,
    resetResponse : resetBookingError,
  } = useHttp();

  const userEmail = useSelector((state) => state.login.userId);

  useEffect(() =>{
    const requestJson = {userEmail};
    fetchBookingDetails(requestJson);
  })

  //Request to backend for bookings data
  const fetchBookingDetails = async (requestJson) => {
    sendBookingRequest({
      url: "http://localhost:8080/fetchBookingDetails",
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
      body: {
        userEmail: requestJson.userEmail,
      },
      errorMsg: "Request failed!",
    });
  };

  const DUMMY_BOOKINGS = [
    {
      bookingId: 1,
      shipName: "Europa One",
      boarding: "Earth",
      arrival: "Europa",
      journeyDate: "06/12/2022",
      duration: "350 hrs",
      price: 310.5,
    },
    {
      bookingId: 2,
      shipName: "Centipede",
      boarding: "Mars",
      arrival: "Moon",
      journeyDate: "06/17/2022",
      duration: "95 hrs",
      price: 155,
    },
  ];

  if (DUMMY_BOOKINGS.length === 0) {
    return <h3 className={classes.ship__fallback}>No bookings available!</h3>;
  }

  return (
    <Layout>
      <ul className={classes.shiplist}>
        {DUMMY_BOOKINGS.map(function (ticket) {
          return (
            <Tickets
              key={ticket.bookingId}
              id={ticket.bookingId}
              name={ticket.shipName}
              boarding={ticket.boarding}
              arrival={ticket.arrival}
              journeyDate={new Date(ticket.journeyDate)}
              duration={ticket.duration}
              price={ticket.price}
            />
          );
        })}
      </ul>
    </Layout>
  );
}
export default MyBookings;
