import Layout from "../layout/Layout";
import { useRef, useState, useReducer } from "react";
import classes from "../Home/Home.module.css";
import styles from "../CommonInput/Input.module.css";
import Card from "../UI/Card/Card";
import Button from "../UI/Button/Button";
import useHttp from "../../hooks/use-http";
import Input from "../CommonInput/Input";

function fromReducer(state, action) {
  if (action.type === "USER_INPUT") {
    return { value: action.val, isValid: action.val.trim() !== "" };
  }
  if (action.type === "INPUT_BLUR") {
    return { value: state.value, isValid: state.value.trim() !== "" };
  }
  if (action.type === "CLEAR") {
    return { value: "", isValid: null };
  }

  return { value: "", isValid: false };
}

function toReducer(state, action) {
  if (action.type === "USER_INPUT") {
    return { value: action.val, isValid: action.val.trim() !== "" };
  }
  if (action.type === "INPUT_BLUR") {
    return { value: state.value, isValid: state.value.trim() !== "" };
  }
  if (action.type === "CLEAR") {
    return { value: "", isValid: null };
  }

  return { value: "", isValid: false };
}

function classReducer(state, action) {
  if (action.type === "USER_INPUT") {
    return { value: action.val, isValid: action.val.trim() !== "" };
  }
  if (action.type === "INPUT_BLUR") {
    return { value: state.value, isValid: state.value.trim() !== "" };
  }
  if (action.type === "CLEAR") {
    return { value: "", isValid: null };
  }

  return { value: "", isValid: false };
}

function journeyDateReducer(state, action) {
  if (action.type === "USER_INPUT") {
    return {
      value: action.val,
      isValid: action.val.trim() !== "",
    };
  }
  if (action.type === "INPUT_BLUR") {
    return {
      value: state.value,
      isValid: state.value.trim() !== "",
    };
  }
  if (action.type === "CLEAR") {
    return { value: "", isValid: null };
  }

  return { value: "", isValid: false };
}

function Home() {
  const [routes, setRoutes] = useState([]);
  const [formIsValid, setFormIsValid] = useState(false);

  const [fromState, dispatchFrom] = useReducer(fromReducer, {
    value: "",
    isValid: null,
  });

  const [toState, dispatchTo] = useReducer(toReducer, {
    value: "",
    isValid: null,
  });

  const [classState, dispatchClass] = useReducer(classReducer, {
    value: "",
    isValid: null,
  });

  const [dateState, dispatchDate] = useReducer(journeyDateReducer, {
    value: "",
    isValid: null,
  });

  const {
    isLoading,
    error,
    sendRequest: sendTaskRequest,
    response,
    resetError,
    resetResponse,
  } = useHttp();

  const fromInputRef = useRef();
  const toInputRef = useRef();
  const classInputRef = useRef();
  const dateInputRef = useRef();

  function fromChangeHandler(event) {
    dispatchFrom({ type: "USER_INPUT", val: event.target.value });
  }
  function validateFromHandler() {
    dispatchFrom({ type: "INPUT_BLUR" });
  }
  function toChangeHandler(event) {
    dispatchTo({ type: "USER_INPUT", val: event.target.value });
  }
  function validateToHandler() {
    dispatchTo({ type: "INPUT_BLUR" });
  }
  function classChangeHandler(event) {
    dispatchClass({ type: "USER_INPUT", val: event.target.value });
  }
  function validateClassHandler() {
    dispatchClass({ type: "INPUT_BLUR" });
  }
  function dateChangeHandler(event) {
    dispatchDate({ type: "USER_INPUT", val: event.target.value });
  }
  function validateDateHandler() {
    dispatchDate({ type: "INPUT_BLUR" });
  }

  //Request to backend for login and sign-up
  const SearchShipsHandler = async (requestJson) => {
    sendTaskRequest({
      url: "http://localhost:8080/searchShips",
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
      body: {
        from: requestJson.enteredName,
        to: requestJson.enteredEmail,
        class: requestJson.enteredClass,
        journeyDate: requestJson.journeyDate,
      },
      errorMsg: "Request failed!",
    });
  };

  return (
    <Layout>
      <section className={classes.summary}>
        <form onSubmit={null}>
          <div className={styles.control}>
            <label>From</label>
            <select
              ref={fromInputRef}
              className={classes.select}
              name="planetList"
              id="planets"
              value={fromState.value}
              onChange={fromChangeHandler}
              onBlur={validateFromHandler}
            >
              <option value="none" style={{ color: "grey" }}>
                Select an Option
              </option>
              <option value="Earth" style={{ color: "black" }}>
                Earth
              </option>
              <option value="Mars" style={{ color: "black" }}>
                Mars
              </option>
              <option value="Moon" style={{ color: "black" }}>
                Moon
              </option>
              <option value="Ceres" style={{ color: "black" }}>
                Ceres
              </option>
              <option value="Europa" style={{ color: "black" }}>
                Europa
              </option>
              <option value="Titan" style={{ color: "black" }}>
                Titan
              </option>
              <option value="EB-15" style={{ color: "black" }}>
                EB-15
              </option>
              <option value="ISS" style={{ color: "black" }}>
                ISS
              </option>
              <option value="Kepler-186F" style={{ color: "black" }}>
                Kepler-186F
              </option>
            </select>
          </div>
          <div className={styles.control}>
            <label>To</label>
            <select
              ref={planetInputRef}
              className={classes.select}
              name="planetList"
              id="planets"
              value={null}
              onChange={null}
              onBlur={null}
            >
              <option value="none" style={{ color: "grey" }}>
                Select an Option
              </option>
              <option value="Earth" style={{ color: "black" }}>
                Earth
              </option>
              <option value="Mars" style={{ color: "black" }}>
                Mars
              </option>
              <option value="Moon" style={{ color: "black" }}>
                Moon
              </option>
              <option value="Ceres" style={{ color: "black" }}>
                Ceres
              </option>
              <option value="Europa" style={{ color: "black" }}>
                Europa
              </option>
              <option value="Titan" style={{ color: "black" }}>
                Titan
              </option>
              <option value="EB-15" style={{ color: "black" }}>
                EB-15
              </option>
              <option value="ISS" style={{ color: "black" }}>
                ISS
              </option>
              <option value="Kepler-186F" style={{ color: "black" }}>
                Kepler-186F
              </option>
            </select>
          </div>
          <div className={styles.control}>
            <label>Class</label>
            <select
              ref={planetInputRef}
              className={classes.select}
              name="planetList"
              id="planets"
              value={null}
              onChange={null}
              onBlur={null}
            >
              <option value="none" style={{ color: "grey" }}>
                Select an Option
              </option>
              <option value="Earth" style={{ color: "black" }}>
                Economy
              </option>
              <option value="Mars" style={{ color: "black" }}>
                Premium
              </option>
              <option value="Mars" style={{ color: "black" }}>
                Executive
              </option>
            </select>
          </div>
          <Input
            ref={null}
            id="date"
            label="Journey Date"
            type="date"
            isValid={null}
            value={null}
            onChange={null}
            onBlur={null}
          ></Input>
          <div className={classes.action}>
            <Button type="submit" className={classes.actions}>
              Search Ships
            </Button>
          </div>
        </form>
      </section>
      <section className={classes.ships}>
        <Card>
          <ul>Hi</ul>
        </Card>
      </section>
    </Layout>
  );
}
export default Home;
