import ShipsCard from "../UI/Card/ShipsCard";
import classes from "./Tickets.module.css";
import ticketClasses from "../UI/Button/Button.module.css";
import TicketDate from "./TicketDate";
import Button from "../UI/Button/Button";
import { useState } from "react";
import Modal from "../UI/Modal";

var ticketId = null;
function Tickets(props) {

  const[showPrompt, setShowPrompt] = useState(false);

  function onTicketCancelHandler(event){
    ticketId = event.target.id;
  }

  const ticketCancelModalActions = (
    <div className={classes.actions}>
      <Button className={classes.btn} onClick={cancellationHandler}>
        Yes
      </Button>
      <Button className={classes.btn} onClick={closeTicketCancelModal}>
        No
      </Button>
    </div>
  );

  const ticketCancelModalContent = (
    <React.Fragment>
      <div className={classes.total}>
        <span>Are you sure? You will be refunded within 5-7 days.</span>
      </div>
    </React.Fragment>
  );

  return (
    <li>
      <ShipsCard className={classes.ship}>
        <TicketDate journeyDate={props.journeyDate} />
        <div>
          <h1>{props.name}</h1>
          <h2>
            {props.boarding} ------------- {props.arrival}
          </h2>
          <h2>{props.duration} hrs</h2>
        </div>
        <div className={classes.ship__price}>${props.price}</div>
        <Button
          id={props.id}
          type="button"
          className={ticketClasses.ticket_button}
          onClick={onTicketCancelHandler}
        >
          Cancel
        </Button>
      </ShipsCard>
    </li>
  );
}
export default Tickets;
